mapper.select:
select sum(amount+penalty_amout) from cl_borrow_repay t0,cl_user t1,cl_channel t2 where t0.user_id = t1.id and t1.channel_id = t2.id and
#{value}
and t0.state = '20' and t0.penalty_amout>0

SELECT SUM(t0.amount) FROM `cl_borrow` t0,cl_user t1,cl_channel t2 WHERE t0.user_id = t1.id and t1.channel_id = t2.id and t0.state NOT IN (10,20,22,21,26,27) and
#{value}
AND t0.address LIKE #{address}

    	SELECT count(t0.user_id) FROM cl_user_base_info t0
    	left join cl_user t1 on t0.user_id = t1.id
    	left join cl_channel t2 on t1.channel_id = t2.id
    	 WHERE

	    				#{value}

    	 	and t0.register_addr LIKE #{address}

select create_time 'key',count(id) 'value' from (select t0.id as id,DATE_FORMAT(t0.create_time,'%Y-%m-%d') as create_time from cl_borrow_progress t0, cl_user t1, cl_channel t2 where t0.user_id = t1.id and t1.channel_id = t2.id and t0.state = '30' and
#{value}
and TO_DAYS(SYSDATE()) - TO_DAYS(t0.create_time) <= 15 ) t GROUP BY create_time

SELECT repay_way 'key', count(id) 'value' FROM ( SELECT t0.id, CASE WHEN t0.repay_way = '10' THEN '自动代扣' WHEN t0.repay_way = '20' THEN '银行卡转账' WHEN t0.repay_way = '30' THEN '支付宝转账' WHEN t0.repay_way = '40' THEN '认证支付' END repay_way FROM cl_borrow_repay_log t0,cl_user t1,cl_channel t2 where t0.user_id = t1.id and t1.channel_id = t2.id and
#{value}
) t GROUP BY repay_way

SELECT DATE_FORMAT(repay_time, '%Y-%m-%d') 'key', count(id) 'value', NOW() FROM ( SELECT t0.id as id, DATE_FORMAT(t0.repay_time, '%Y-%m-%d') repay_time FROM cl_borrow_repay t0,cl_user t1, cl_channel t2 WHERE t0.user_id = t1.id and t1.channel_id = t2.id and
#{value}
and DATE_FORMAT(NOW(), '%Y-%m-%d') >= DATE_FORMAT(t0.repay_time, '%Y-%m-%d') ) t GROUP BY repay_time

select create_time 'key',count(id) 'value' from (select t0.id as id,DATE_FORMAT(t0.repay_time,'%Y-%m-%d') create_time from cl_borrow_repay_log t0,cl_user t1,cl_channel t2 where t0.user_id = t1.id and t1.channel_id = t2.id and
#{value}
and TO_DAYS(SYSDATE()) - TO_DAYS(t0.repay_time) <= 15 ) t GROUP BY create_time
